// ================================
// ✅ server.js (Backend Express)
// ================================
import express from "express";
import cors from "cors";
import db from "./configs/data.js"; // Kết nối MySQL (đã dùng mysql2/promise)

const app = express();
const PORT = 8080;

// Cho phép React truy cập API
app.use(
  cors({
    origin: "http://localhost:3000", // Cổng frontend React
  })
);

app.use(express.json());

/* ===========================
   1️⃣ API: Lấy danh sách tất cả công việc
=========================== */
app.get("/jobs", async (req, res) => {
  try {
    const sql = `
      SELECT 
        jp.id,
        jp.job_title AS title,
        c.company_name AS company,
        jl.city AS location,
        jp.salary,
        jp.created_date AS posted_date,
        jp.category_id AS industry_id
      FROM job_post jp
      LEFT JOIN company c ON jp.company_id = c.id
      LEFT JOIN job_location jl ON jp.job_location_id = jl.id
      ORDER BY jp.created_date DESC;
    `;
    const [rows] = await db.query(sql);
    res.json(rows);
  } catch (err) {
    console.error("❌ Lỗi truy vấn jobs:", err);
    res.status(500).json({ message: "Lỗi truy vấn jobs" });
  }
});

/* ===========================
   2️⃣ API: Lấy danh sách ngành nghề
=========================== */
app.get("/industries", async (req, res) => {
  try {
    const [rows] = await db.query(
      "SELECT id, name FROM category ORDER BY name ASC"
    );
    res.json(rows);
  } catch (err) {
    console.error("❌ Lỗi truy vấn industries:", err);
    res.status(500).json({ message: "Lỗi truy vấn industries" });
  }
});

/* ===========================
   3️⃣ API: Lấy chi tiết 1 công việc theo ID
=========================== */
app.get("/api/jobs/:id", async (req, res) => {
  try {
    const jobId = req.params.id;
    const [rows] = await db.query(
      `
        SELECT 
          jp.id,
          jp.job_title AS title,
          c.company_name AS company,
          jl.city AS location,
          jp.salary,
          jp.created_date AS posted_date,
          jp.category_id AS industry_id,
          jp.description,
          jp.requirements,
          jp.benefits
        FROM job_post jp
        LEFT JOIN company c ON jp.company_id = c.id
        LEFT JOIN job_location jl ON jp.job_location_id = jl.id
        WHERE jp.id = ?;
      `,
      [jobId]
    );

    if (rows.length === 0)
      return res.status(404).json({ message: "Không tìm thấy công việc" });

    res.json(rows[0]);
  } catch (err) {
    console.error("❌ Lỗi truy vấn chi tiết job:", err);
    res.status(500).json({ message: "Lỗi truy vấn job" });
  }
});

/* ===========================
   4️⃣ API kiểm tra kết nối CSDL
=========================== */
app.get("/experience", async (req, res) => {
  try {
    const [rows] = await db.query("SELECT * FROM experience_detail");
    res.json(rows);
  } catch (err) {
    console.error("❌ Lỗi truy vấn experience:", err);
    res.status(500).json({ message: "Lỗi truy vấn CSDL" });
  }
});

/* =====================================================
   5️⃣ API: Lấy chi tiết công việc (JobDetail page)
===================================================== */
app.get("/jobs/:id", async (req, res) => {
  const jobId = req.params.id;

  const sql = `
    SELECT 
      jp.id,
      jp.job_title AS title,
      jp.job_description AS description,
      jp.requirements,
      jp.salary,
      jp.created_date AS posted_date,
      jp.date_expire AS expire_date,
      c.company_name AS company,
      c.profile_description AS company_description,
      c.company_website_url AS company_website,
      jl.city AS location,
      cat.name AS industry_name
    FROM job_post jp
    LEFT JOIN company c ON jp.company_id = c.id
    LEFT JOIN job_location jl ON jp.job_location_id = jl.id
    LEFT JOIN category cat ON jp.category_id = cat.id
    WHERE jp.id = ?;
  `;

  try {
    const [result] = await db.query(sql, [jobId]);
    if (result.length === 0) {
      return res.status(404).json({ message: "Không tìm thấy công việc" });
    }
    res.json(result[0]);
  } catch (err) {
    console.error("❌ Lỗi truy vấn chi tiết công việc:", err);
    res.status(500).json({
      message: "Lỗi khi lấy chi tiết công việc",
      error: err.message,
    });
  }
});

/* ===========================
   ✅ Khởi động server
=========================== */
app.listen(PORT, () => {
  console.log(`🚀 Server Node.js đang chạy tại: http://localhost:${PORT}`);
});

