import mysql from "mysql2/promise";

// ✅ Tạo pool (tự động quản lý kết nối, không cần await ở top-level)
const db = mysql.createPool({
  host: "localhost",
  user: "root",
  password: "",
  database: "job_db",
  port: 3306,
  waitForConnections: true,
  connectionLimit: 10,
  queueLimit: 0,
});

// ✅ Kiểm tra kết nối (chạy 1 lần khi khởi động)
db.getConnection()
  .then(() => console.log("✅ Đã kết nối thành công tới MySQL (phpMyAdmin)"))
  .catch((err) => console.error("❌ Lỗi kết nối MySQL:", err.message));

export default db;
